import React, { useState } from "react";
import axios from "axios";
import style from "./CreatePothole.module.scss"

function CreatePothole({ token }) {
  const [street, setStreet] = useState("");
  const [longitude, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [level, setLevel] = useState("");

  const handleCreate = async () => {
    try {
      const response = await axios.post(
        "http://localhost:8000/v1/pothole/create",
        { street, longitude, latitude, level },
        { headers: { token: `Bearer ${token}` } }
      );
      alert("Pothole created successfully!");
      console.log(response.data);
    } catch (err) {
      alert("Failed to create pothole!");
      console.error(err);
    }
  };

  return (
    <div>
      <h2 className= {style.header}>Create Pothole</h2>
      <input
        type="text"
        placeholder="Street"
        value={street}
        onChange={(e) => setStreet(e.target.value)}
        style={{ margin: "5px" }}
      />
      <input
        type="text"
        placeholder="Longitude"
        value={longitude}
        onChange={(e) => setLongitude(e.target.value)}
      />
      <input
        type="text"
        placeholder="Latitude"
        value={latitude}
        onChange={(e) => setLatitude(e.target.value)}
      />
      <input
        type="text"
        placeholder="Level"
        value={level}
        onChange={(e) => setLevel(e.target.value)}
      />
      <button onClick={handleCreate}>Create Pothole</button>
    </div>
  );
}

export default CreatePothole;
